import java.util.Arrays;
import java.util.Comparator;
import java.util.PriorityQueue;

class Solution {
    public int mostBooked(int n, int[][] meetings) {
        Arrays.sort(meetings, Comparator.comparingInt(a->a[0]));
        int[] cnt = new int[n];
        PriorityQueue<int[]> queue = new PriorityQueue<>((a, b) -> {
            if (a[1] == b[1]) {
                return a[0] - b[0];
            }
            return a[1] - b[1];
        });
        for (int i = 0; i < n; i++) {
            queue.offer(new int[]{i, meetings[0][0]});
        }
        for (int[] meeting : meetings) {
            while (queue.peek()[1]<meeting[0]){
                int[] poll = queue.poll();
                poll[1] = meeting[0];
                queue.offer(poll);
            }
            int[] poll = queue.poll();
            poll[1] += meeting[1] - meeting[0];
            queue.offer(poll);
            cnt[poll[0]]++;
        }
        int ans = 0, c = cnt[0];
        for (int i = 1; i < cnt.length; i++) {
            if(cnt[i] > c){
                ans = i;
                c = cnt[i];
            }
        }
        return ans;
    }
}
```

